import { Component } from 'react';
import './ExperienceCard.css';
import { Fade } from "react-reveal";
import Avatar from '@mui/joy/Avatar';
import * as React from 'react';
import AspectRatio from '@mui/joy/AspectRatio';
import Link from '@mui/joy/Link';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import Chip from '@mui/joy/Chip';
import Typography from '@mui/joy/Typography';
import Button from '@mui/joy/Button';

class ExperienceCard extends Component {
  render() {
    const experience = this.props.experience;
    const index = this.props.index;
    const totalCards = this.props.totalCards;
    const theme = this.props.theme;
    return (
      <div className="experience-list-item" style={{ marginTop: (index === 0 ? 30 : 50) }}>
        <Fade left duration={2000} distance="40px">
          <div className="experience-card-logo-div">
            {/** 
            <img
              
              className="experience-card-logo"
              src={require(`../../assests/images/${experience["logo_path"]}`)}
              alt=""
            />  */}
            <Avatar
  color="primary"
  size="sm"
              variant="outlined"
              src={require(`../../assests/images/${experience["logo_path"]}`)}
              sx={{
                "--Avatar-size": "57px",
               
              }}

              style={{
                
                
                backgroundColor: "white",
               
              }}
/>
          </div>
        </Fade>
        <div className="experience-card-stepper">
          <div style={{ width: 20, height: 20, backgroundColor:`${theme.headerColor}`, borderRadius: 50, zIndex: 100 }} />
          {
            index !== (totalCards - 1) &&
            <div style={{ height: 190, width: 2, backgroundColor: `${theme.headerColor}`, position: 'absolute', marginTop: 20 }} />
          }
        </div>
        <Fade right duration={2000} distance="40px">
          <div style={{ display: 'flex', flexDirection: 'row' }}>
            <div className="arrow-left" style={{ borderRight: `10px solid lightblue` }}></div>
            <Card
      color="primary"
      variant="outlined"
          orientation="horizontal"
          sx={{
        
        
        '&:hover': { boxShadow: 'md', borderColor: 'neutral.outlinedHoverBorder' },
        marginBottom:5,
      }}
            >
            {/** 
      <AspectRatio ratio="1" sx={{ width:140 }}>
        <img
        src={require(`../../assests/images/${degree.logo_path}`)}
        alt={degree.alt_name}
        loading="lazy"
          
        />
      </AspectRatio>     */}  
      <CardContent>
        <Typography level="title-lg" id="card-description">
        {degree.title}
        </Typography>
        <Typography level="body-sm" aria-describedby="card-description" mb={1}>
          
             {degree.subtitle}
         
          </Typography>
          {degree.descriptions.map((sentence) => {
                return (
       <Typography>
           {sentence}
           </Typography>
                       );
                      })}
          {degree.website_link && (
                <a
                  href={degree.website_link}
                  target="_blank"
              rel="noopener noreferrer"
              style={{textAlign:"right"}}
                >
                 
                    <Button  color="primary" variant="outlined" >
                      Visit Website
                    </Button>
                  
                </a>
              )}
      </CardContent>
        </Card>

          </div>
        </Fade>
      </div>

    );
  }
}

export default ExperienceCard;
